
#include <stdlib.h>
#include <stdio.h>
#include <math.h>
#include <string.h>

/*
给定两个字符串 s 和 p，找到 s 中所有 p 的 异位词 的子串，返回这些子串的起始索引。不考虑答案输出的顺序。

异位词 指字母相同，但排列不同的字符串。



来源：力扣（LeetCode）
链接：https://leetcode-cn.com/problems/find-all-anagrams-in-a-string
著作权归领扣网络所有。商业转载请联系官方授权，非商业转载请注明出处。
*/
int charcmp(char* a, char* b){return *a - *b;}
int* findAnagrams0(char * s, char * p, int* returnSize){
    int l = strlen(p);
    int sl = strlen(s);
    if(l == 0 || sl == 0)return 0;

    // printf("p :%s\n", p);
    qsort(p, l, sizeof(char), charcmp);
    int i = 0, c = 0;
    int* r = malloc(sl*sizeof(int));
    char t[l+1];
    t[l] = 0;
    // printf("ps:%s\n", p);
    while(i < sl-l+1){
        memcpy(t, &s[i], l);
        qsort(t, l, sizeof(char), charcmp);
        if(strcmp(t, p) == 0){
            printf("%d:%d\n",c, i);
            r[c++] = i;
        }
        ++i;
    }
    *returnSize = c;
    return r;
}
#define LETTER_SIZE 26
int *findAnagrams(char *s, char *p, int *returnSize)
{
    *returnSize = 0;
    int sLen = strlen(s);
    int pLen = strlen(p);
    int currLen = 0;
    int sCount[LETTER_SIZE] = {0};
    int pCount[LETTER_SIZE] = {0};
    int *res = NULL;

    if (s == NULL || p == NULL || sLen == 0 || pLen == 0) {
        return NULL;
    }

    for (int i = 0; i < pLen; ++i) {
        pCount[p[i] - 'a']++;
    }

    res = (int *)malloc(sizeof(int) * sLen);
    if (res == NULL) {
        return NULL;
    }
    memset(res, 0, sizeof(int) * sLen);

    int i = 0;
    for (int j = 0; j < sLen; ++j) {
        i = s[j] - 'a';
        sCount[i]++;
        currLen++;

        // printf(">%d:%s\n", j, &s[j]);
        //当前字符出现次数大于模式串中的次数
        while (sCount[i] > pCount[i]) {
            //
            sCount[s[j - currLen + 1] - 'a']--;//
            currLen--;
            // printf("\t%c currLen:%d, %d>%d %s:%d\n"
            //     ,     s[j], currLen
            //     ,              sCount[i], pCount[i]
            //     , &s[j - currLen + 1] ,sCount[s[j - currLen + 1] - 'a']);
        }

        if (currLen == pLen) {
            printf("\t-->%d %d\n", currLen, j - currLen + 1);
            res[(*returnSize)++] = j - currLen + 1;
        }
    }
    return res;
}

int main(int c, char** v){
    char s[] = 
         "";
    char p[] = 
        "";
    
    
    // char s[] = "baaabddaddabbbddcccbabaa";
    // char p[] = "add";
    int rs = 0;
    int* r = findAnagrams0(s, p, &rs);
    for(int i = 0; i < rs; ++i){
        printf("%d,", r[i]);
    }
    printf("\n");

    return 0;
}